package com.myivcre.ga.model;import java.util.List;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.OneToMany;/** * 图片新闻 * @author freepander * */@Entity(name="picturenews")public class PictureNews {	@Id@GeneratedValue	private int id;	private String title;	private int number;	@OneToMany	private List<PictureNewsItem> items;	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public int getNumber() {		return number;	}	public void setNumber(int number) {		this.number = number;	}	public List<PictureNewsItem> getItems() {		return items;	}	public void setItems(List<PictureNewsItem> items) {		this.items = items;	}	}