package com.myivcre.ga.model;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;/** * 图片新闻 项 * @author freepander * */@Entity(name="picturenewsitem")public class PictureNewsItem {	@Id@GeneratedValue	private int id;	private String img;	private String description;	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public String getImg() {		return img;	}	public void setImg(String img) {		this.img = img;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	}